mapper.update:
    	UPDATE 	SAVINGS
		SET 	SA_AM = #{sa_am}
					+ (SELECT SA_AM 
						FROM 	SAVINGS 
						WHERE 	USER_ID = #{user_id} 
						AND 	TO_CHAR(SA_DT, 'RRRR-MM-DD') = TO_CHAR(SYSDATE, 'RRRR-MM-DD'))
		WHERE 	USER_ID = #{user_id}
		AND 	TO_CHAR(SA_DT, 'RRRR-MM-DD') = TO_CHAR(SYSDATE, 'RRRR-MM-DD')

mapper.select:
SELECT 	SUM(SA_AM)
FROM 	SAVINGS
WHERE 	USER_ID = #{user_id}

mapper.update:
UPDATE 	SAVINGS
SET 	SA_AM = 0
WHERE 	USER_ID = #{user_id}
